            {
                Status = __status,
                Expression = __messageToSend,
                Indexes = __indexes,
                DataValidationMessageType = DataValidationMessageType.ERROR
            });
            return new RuleResult
            {
                Name = "IsMailValidValidation",
                Evaluations = __evaluations
            };
        }

        #endregion
    }
}
