        private Func<object, int[], string> Get_FileUpload_RelativePath
        {
            get
            {
                return (object _model, int[] indexes) =>
                {
                    var @model = _model as ThemePreviewViewModel;
                    return "ThemeTest"  + "/" +  Path.GetFileName(@model.JustString);
